import { Component } from '@angular/core';
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {
  title = 'toDoFront';
  constructor(private http: HttpClient) {}

  ngOnInit(){
    //this.updateLibros().subscribe();
  }

  tryAdd(){
    this.getFrase().subscribe()
  }

  tryUpdate(){
    this.updateLibros().subscribe()
  }

  public getFrase(): Observable<any> {
    const payload = {
      "title": "maria1",
      "text": "111111111"
    }
    const url = "https://us-central1-atomchallengetodo.cloudfunctions.net/app/addTask"
    return this.http.post<any>(url, payload );
  }

  updateLibros(): Observable<any>{
      const payload = {
       "id": "31PS4zMyf3AZfUbrSElw",
       "title": "mariaEditado",
       "text": "test"
      }
      const url = "https://us-central1-atomchallengetodo.cloudfunctions.net/app/updateTask/"+payload.id
      return this.http.put<any>(url, payload );
  }

  public getlibros(): Observable<any> {
    const url = "https://us-central1-atomchallengetodo.cloudfunctions.net/app/guardarLibro"
    return this.http.get<any>(url);
  }
}


