import { Component, OnInit } from '@angular/core';
import {
  CdkDragDrop,
  CdkDrag,
  CdkDropList,
  CdkDropListGroup,
  moveItemInArray,
  transferArrayItem,
} from '@angular/cdk/drag-drop';
import { TaskService } from 'src/services/tasks/task.service';
import { TaskItem } from '../interfaces/task.interface';

@Component({
  selector: 'app-task-item',
  templateUrl: './task-item.component.html',
  styleUrls: ['./task-item.component.css']
})

export class TaskItemComponent implements OnInit {

taskLs: TaskItem[] = []

  constructor(private taskService$ : TaskService) {}

  ngOnInit(): void {
    this.getAllTasks();
  }
  todo = ['Get to work', 'Pick up groceries aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa', 'Go home', 'Fall asleep'];

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.taskLs, event.previousIndex, event.currentIndex);
  }

  getAllTasks(){
      this.taskService$.getAllTasks().subscribe( (tasks:TaskItem[]) => {
          this.taskLs = [...tasks];
      })
  }
}
