import { ChangeDetectionStrategy, Component, Input, OnInit } from '@angular/core';
import {
  CdkDragDrop,
  CdkDrag,
  CdkDropList,
  CdkDropListGroup,
  moveItemInArray,
  transferArrayItem,
} from '@angular/cdk/drag-drop';
import { TaskService } from 'src/services/tasks/task.service';
import { TaskItem } from '../interfaces/task.interface';
import Swal from 'sweetalert2'

@Component({
  selector: 'app-task-item',
  templateUrl: './task-item.component.html',
  styleUrls: ['./task-item.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})

export class TaskItemComponent implements OnInit {

  @Input() taskLs: TaskItem[] = []

  constructor(private taskService$: TaskService) { }

  ngOnInit(): void {
  }

  drop(event: CdkDragDrop<TaskItem[]>) {
    moveItemInArray(this.taskLs, event.previousIndex, event.currentIndex);
  }


  async launchFormEdit() {
    const { value: formValues } = await Swal.fire({
      title: 'Editar Tarea',
      html:
      '<label>Titulo:</label>' +
        '<input id="swal-input1" class="swal2-input">' +
        '<label style="margin-top=5px">Descripcion:</label>' +
        '<input style="overflow:auto;resize:none" maxlength="70" id="swal-input2" class="swal2-input">',
      focusConfirm: false,
      showCancelButton: true,
      confirmButtonColor: '#FF8D3B',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Editar',
      cancelButtonText: 'Cancelar',
      preConfirm: () => {
        return {
          title: (document.getElementById('swal-input1') as HTMLInputElement).value,
          description: (document.getElementById('swal-input2') as HTMLInputElement).value
        }

      }
    })

    if (formValues) {
      console.log(formValues)
      this.taskService$.updateTask(formValues).subscribe()
    }
  }
}
